import{_ as a,o as e,c as r,R as t}from"./chunks/framework.7M7mM_5n.js";const l="/skylark-doc/assets/framework.pr_TrzNk.png",b=JSON.parse('{"title":"前言","description":"","frontmatter":{},"headers":[],"relativePath":"preface.md","filePath":"preface.md"}'),o={name:"preface.md"},i=t('<h1 id="前言" tabindex="-1">前言 <a class="header-anchor" href="#前言" aria-label="Permalink to &quot;前言&quot;">​</a></h1><blockquote><p>更新时间：2023-12-04</p></blockquote><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p><a href="https://github.com/delav/skylark-doc" target="_blank" rel="noreferrer">Skylark</a> 是一个以功能组件的方式编写测试用例的开源分布式自动化测试平台。理论上可支持任何类型的自动化测试，包括接口测试、 集成测试、UI测试、数据库测试等。</p><h3 id="特点" tabindex="-1">特点 <a class="header-anchor" href="#特点" aria-label="Permalink to &quot;特点&quot;">​</a></h3><ul><li><code>独特的用例编写方式。</code>功能组件是组成自动化测试用例的基本单元，通过拖拽移动+填写参数即可编写用例。</li><li><code>高可扩展性。</code>业务和平台分离，如需任何功能，开发相应的功能组件（python函数）即可，可用于复杂的业务。</li><li><code>高可读性。</code>用例可视化，所见即所得，无论任何人都可以清晰的看到用例的实现内容，利于团队协作。</li><li><code>低门槛，易使用。</code>无任何编程基础知识，关注的是你的自动化测试设计过程，而非脚本或者代码实现。</li><li><code>分布式运行。</code>支持一个项目分多个批次同时执行（多机器、多进程），可大大降低自动化执行所需的时间。</li></ul><h2 id="架构" tabindex="-1">架构 <a class="header-anchor" href="#架构" aria-label="Permalink to &quot;架构&quot;">​</a></h2><h3 id="整体框架" tabindex="-1">整体框架 <a class="header-anchor" href="#整体框架" aria-label="Permalink to &quot;整体框架&quot;">​</a></h3><p><img src="'+l+'" alt=""></p><h3 id="组成部分" tabindex="-1">组成部分 <a class="header-anchor" href="#组成部分" aria-label="Permalink to &quot;组成部分&quot;">​</a></h3><p>综合多种因素考虑后，组成大概分为四个部分：</p><ul><li><a href="https://github.com/delav/skylark-web" target="_blank" rel="noreferrer">skylark-web</a>：基于Vue开发的用户交互界面</li><li><a href="https://github.com/delav/skylark" target="_blank" rel="noreferrer">skylark</a>：基于Django开发的后端服务</li><li><a href="https://github.com/delav/skylarkworker" target="_blank" rel="noreferrer">skylarkwork</a>：专门用于执行测试任务</li><li><a href="https://github.com/delav/skylarklibrary" target="_blank" rel="noreferrer">skylarklibrary</a>：用于开发和迭代功能组件</li></ul><div class="tip custom-block"><p class="custom-block-title">说明</p><p>仓库暂时未开放</p></div>',13),c=[i];function s(h,d,n,k,p,_){return e(),r("div",null,c)}const f=a(o,[["render",s]]);export{b as __pageData,f as default};
